body {
  font-family: "Inter", system-ui, sans-serif;
}

.waveform-widget {
  font-family: "Inter", system-ui, sans-serif;
  background: linear-gradient(112deg, #f8fafc 72%, #e6f7fa 100%);
  padding: 32px 40px 28px 40px;
  border-radius: 18px;
  box-shadow: 0 4px 24px rgba(38, 125, 255, 0.06), 0 1.5px 4px rgba(75, 80, 90, 0.03);
  box-sizing: border-box;
  max-width: 580px;
  min-width: 360px;
  margin: 0 auto;
  border: 1px solid #e5effb;
}

.section-title {
  font-size: 18.5px;
  font-weight: 700;
  margin-top: 18px;
  margin-bottom: 9px;
  color: #1f2937;
  letter-spacing: 0.015em;
  display: flex;
  align-items: center;
  gap: 6px;
}

.section-divider {
  border-bottom: 1.5px solid #e3e9f5;
  margin: 18px 0 9px 0;
}

.data-grid {
  display: grid;
  grid-template-columns: max-content 1fr;
  gap: 13px 26px;
  font-size: 15.2px;
  margin-bottom: 17px;
}

.data-label {
  color: #5d6e85;
  font-weight: 600;
  text-align: right;
  letter-spacing: 0.01em;
  align-self: center;
  padding-right: 6px;
}

.data-value {
  align-self: center;
}

.data-input {
  font-family: "Inter", system-ui, sans-serif;
  font-size: 15.2px;
  background: #f3f7fa;
  border: 1.6px solid #dae6f3;
  color: #384868;
  font-weight: 500;
  border-radius: 6.2px;
  min-width: 60px;
  padding: 2.2px 11px 2.2px 10px;
  pointer-events: none;
  outline: none;
  box-shadow: none;
  appearance: textfield;
  text-align: right;
  margin-left: 2px;
}

.status-pill {
  margin-left: 8px;
  background: #ecf8f8;
  color: #08816a;
  font-size: 14px;
  font-weight: 600;
  border-radius: 14px;
  display: inline-block;
  padding: 3.7px 15px;
  vertical-align: middle;
  border: none;
  box-shadow: 0 1px 1.5px rgba(143, 227, 231, 0.6588235294);
  letter-spacing: 0.01em;
}

.status-pill.unstable {
  background: #fff2ec;
  color: #ea544a;
}

.status-pill.stable {
  background: #e3fbea;
  color: #159a61;
}

.warning-list {
  background: linear-gradient(104deg, #fffbe7 85%, #fff2d6 100%);
  color: #b77a00;
  border: 1px solid #ffe4b2;
  border-radius: 7px;
  font-size: 14.7px;
  padding: 12px 27px 10px 22px;
  margin-top: 10px;
  margin-bottom: 17px;
  line-height: 1.67;
  box-shadow: 0 2px 7px rgba(255, 238, 204, 0.2196078431);
}

.chart-wrapper {
  background: #fff;
  border-radius: 10px;
  box-shadow: 0 1.5px 7px rgba(207, 235, 255, 0.1529411765);
  padding: 12px 5px 8px 5px;
  margin: 18px auto 16px auto;
  min-width: 0;
  max-width: 410px;
  width: 99%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}

.chart-title {
  color: #1273de;
  font-size: 14px;
  font-weight: 700;
  margin-bottom: 0px;
  margin-left: 2px;
  letter-spacing: 0.01em;
  text-align: center;
}

.chart-wrapper canvas {
  display: block;
  margin: 0 auto;
  width: 99% !important;
  height: 160px !important;
  max-width: 100% !important;
  min-width: 120px !important;
  background: #fff;
  border-radius: 7px;
}

.droplet-sat {
  color: #d44f24;
  font-weight: 700;
  font-size: 14px;
  margin-top: 4px;
  display: inline-block;
  letter-spacing: 0.01em;
}

.recommend-block {
  margin-top: 17px;
  padding: 19px 22px 14px 16px;
  background: linear-gradient(98deg, #ecf8ff 80%, #e7fce3 100%);
  border-radius: 11px;
  border: 1px solid #bde6fa;
  box-shadow: 0 1.5px 3px rgba(189, 230, 250, 0.2666666667);
}

.recommend-block .section-title {
  font-size: 16.5px;
  margin-bottom: 8px;
  color: #0a3e82;
  letter-spacing: 0.006em;
}

@media (max-width: 600px) {
  .waveform-widget {
    max-width: 98vw !important;
    padding: 16px 6vw 18px 6vw;
    min-width: 0 !important;
  }
  .chart-wrapper {
    max-width: 99vw;
    width: 99vw;
    min-width: 0;
    padding: 10px 1vw 10px 1vw;
  }
  .chart-wrapper canvas {
    width: 98vw !important;
    min-width: 0 !important;
    height: 135px !important;
  }
}

