          'Dwell time (μs)': waveform.dwellTime_us,
          'Recovery time (μs)': waveform.recoveryTime_us,
          'Peak Pressure (Pa)': waveform.peakPressure_Pa,
          'Negative Peak Pressure (Pa)': waveform.negativePeakPressure_Pa,
          'Shape': waveform.shape
        } : {}} />
      </Section>
      <Section icon={ICONS.drop} title="Predicted Droplet">
        <DataGrid data={droplet ? {
          'Size (μm)': droplet.size_um,
          'Velocity (m/s)': droplet.velocity_mps,
          'Break-off Time (μs)': droplet.breakOffTime_us,
          'Stability': droplet.stabilityClass
        } : {}} />
        <DropletSat hasSatellite={droplet && droplet.hasSatellite} />
      </Section>
